mapper.select:
select su.id id, su.NAME name, su.PASSWORD password, su.salt salt, su.email email, su.mobile mobile, su.STATUS STATUS, su.dept_id dept_id, su.create_by create_by, su.create_time create_time, su.last_update_by last_update_by, su.last_update_time last_update_time, su.del_flag del_flag, sur.role_id role_id from sys_user su left join sys_user_role sur on su.id = sur.user_id where 1=1 and sur.del_flag = 0
and name = #{name}
and password = #{password}
and mobile = #{mobile}

mapper.update:
